import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { 
  Sparkles, 
  FileText, 
  Eye, 
  Save, 
  Wand2, 
  Lightbulb,
  RefreshCw,
  Copy,
  PenTool
} from "lucide-react";

interface AIBlogEditorProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  blog?: any;
  onBlogSaved: () => void;
}

export default function AIBlogEditor({ open, onOpenChange, blog, onBlogSaved }: AIBlogEditorProps) {
  const { toast } = useToast();
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    title: "",
    content: "",
    excerpt: "",
    published: false,
  });
  const [aiPrompt, setAiPrompt] = useState("");
  const [activeTab, setActiveTab] = useState("write");
  const [isGenerating, setIsGenerating] = useState(false);

  // Initialize form with existing blog data
  useEffect(() => {
    if (blog) {
      setFormData({
        title: blog.title || "",
        content: blog.content || "",
        excerpt: blog.excerpt || "",
        published: blog.published || false,
      });
    } else {
      setFormData({
        title: "",
        content: "",
        excerpt: "",
        published: false,
      });
    }
  }, [blog]);

  // Save blog mutation
  const saveBlogMutation = useMutation({
    mutationFn: async (data: any) => {
      if (blog?.id) {
        return apiRequest("PUT", `/api/blogs/${blog.id}`, data);
      } else {
        return apiRequest("POST", "/api/blogs", data);
      }
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: blog?.id ? "Blog updated successfully" : "Blog created successfully",
      });
      onBlogSaved();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to save blog",
        variant: "destructive",
      });
    },
  });

  // Generate blog content mutation
  const generateContentMutation = useMutation({
    mutationFn: async (prompt: string) => {
      return apiRequest("POST", "/api/blogs/generate", { 
        prompt, 
        title: formData.title || "AI Generated Blog Post" 
      });
    },
    onSuccess: (data) => {
      setFormData(prev => ({
        ...prev,
        title: data.title,
        content: data.content,
        excerpt: data.excerpt || data.content.slice(0, 150) + "...",
      }));
      setIsGenerating(false);
      setActiveTab("write");
      toast({
        title: "Success",
        description: "AI content generated successfully",
      });
    },
    onError: (error) => {
      setIsGenerating(false);
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to generate content",
        variant: "destructive",
      });
    },
  });

  // Get content ideas mutation
  const getIdeasMutation = useMutation({
    mutationFn: async (topic: string) => {
      return apiRequest("POST", "/api/ai/content-ideas", { 
        topic, 
        type: "blog" 
      });
    },
    onSuccess: (data) => {
      const ideas = data.ideas.join('\n\nâ€¢ ');
      toast({
        title: "Content Ideas Generated",
        description: "Check the AI tab for your content ideas",
      });
      setAiPrompt(`Here are some blog ideas for "${data.topic}":\n\nâ€¢ ${ideas}`);
      setActiveTab("ai");
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to generate ideas",
        variant: "destructive",
      });
    },
  });

  const handleInputChange = (field: string, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleGenerateContent = () => {
    if (!aiPrompt.trim()) {
      toast({
        title: "Error",
        description: "Please enter a prompt for AI content generation",
        variant: "destructive",
      });
      return;
    }

    setIsGenerating(true);
    generateContentMutation.mutate(aiPrompt);
  };

  const handleGetIdeas = () => {
    if (!formData.title.trim()) {
      toast({
        title: "Error",
        description: "Please enter a blog title first to get content ideas",
        variant: "destructive",
      });
      return;
    }

    getIdeasMutation.mutate(formData.title);
  };

  const handleSave = () => {
    if (!formData.title.trim() || !formData.content.trim()) {
      toast({
        title: "Error",
        description: "Please fill in title and content",
        variant: "destructive",
      });
      return;
    }

    saveBlogMutation.mutate({
      ...formData,
      aiGenerated: !!blog?.aiGenerated || generateContentMutation.isSuccess,
    });
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: "Copied",
      description: "Content copied to clipboard",
    });
  };

  const quickPrompts = [
    "Write a comprehensive guide about productivity tips for remote workers",
    "Create a beginner's tutorial on digital marketing strategies",
    "Explain the benefits of AI in modern business operations",
    "Write about the latest trends in web development",
    "Create a how-to guide for starting a successful freelance career",
  ];

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-5xl max-h-[95vh] overflow-hidden flex flex-col" data-testid="ai-blog-editor">
        <DialogHeader className="pb-4 border-b">
          <div className="flex items-center justify-between">
            <DialogTitle className="flex items-center">
              <Sparkles className="w-5 h-5 mr-2 text-primary" />
              {blog?.id ? "Edit Blog Post" : "Create New Blog Post"}
            </DialogTitle>
            <Badge variant="secondary" className="flex items-center">
              <Wand2 className="w-3 h-3 mr-1" />
              AI Powered
            </Badge>
          </div>
        </DialogHeader>

        <div className="flex-1 overflow-hidden">
          <Tabs value={activeTab} onValueChange={setActiveTab} className="h-full flex flex-col">
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="write" className="flex items-center" data-testid="tab-write">
                <PenTool className="w-4 h-4 mr-2" />
                Write
              </TabsTrigger>
              <TabsTrigger value="ai" className="flex items-center" data-testid="tab-ai">
                <Sparkles className="w-4 h-4 mr-2" />
                AI Assistant
              </TabsTrigger>
              <TabsTrigger value="preview" className="flex items-center" data-testid="tab-preview">
                <Eye className="w-4 h-4 mr-2" />
                Preview
              </TabsTrigger>
            </TabsList>

            <div className="flex-1 overflow-y-auto mt-4">
              {/* Write Tab */}
              <TabsContent value="write" className="space-y-4 h-full" data-testid="write-content">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="blog-title">Title *</Label>
                    <Input
                      id="blog-title"
                      value={formData.title}
                      onChange={(e) => handleInputChange("title", e.target.value)}
                      placeholder="Enter your blog post title"
                      data-testid="input-blog-title"
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="blog-excerpt">Excerpt</Label>
                    <Input
                      id="blog-excerpt"
                      value={formData.excerpt}
                      onChange={(e) => handleInputChange("excerpt", e.target.value)}
                      placeholder="Brief description or summary"
                      data-testid="input-blog-excerpt"
                    />
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="blog-content">Content *</Label>
                  <Textarea
                    id="blog-content"
                    value={formData.content}
                    onChange={(e) => handleInputChange("content", e.target.value)}
                    placeholder="Write your blog content here. You can use markdown formatting."
                    className="min-h-[400px] font-mono"
                    data-testid="textarea-blog-content"
                  />
                  <p className="text-xs text-muted-foreground">
                    Supports Markdown formatting. Character count: {formData.content.length}
                  </p>
                </div>

                <div className="flex items-center justify-between p-4 bg-muted/50 rounded-lg">
                  <div>
                    <Label htmlFor="blog-published">Publish immediately</Label>
                    <p className="text-sm text-muted-foreground">
                      Make this blog post visible to everyone
                    </p>
                  </div>
                  <Switch
                    id="blog-published"
                    checked={formData.published}
                    onCheckedChange={(checked) => handleInputChange("published", checked)}
                    data-testid="switch-blog-published"
                  />
                </div>
              </TabsContent>

              {/* AI Assistant Tab */}
              <TabsContent value="ai" className="space-y-4" data-testid="ai-content">
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <h3 className="text-lg font-semibold">AI Writing Assistant</h3>
                    <Button 
                      variant="outline" 
                      size="sm" 
                      onClick={handleGetIdeas}
                      disabled={getIdeasMutation.isPending || !formData.title.trim()}
                      data-testid="button-get-ideas"
                    >
                      <Lightbulb className="w-4 h-4 mr-2" />
                      Get Ideas
                    </Button>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="ai-prompt">AI Prompt</Label>
                    <Textarea
                      id="ai-prompt"
                      value={aiPrompt}
                      onChange={(e) => setAiPrompt(e.target.value)}
                      placeholder="Describe what you want to write about. Be specific about the topic, tone, and target audience."
                      className="min-h-[120px]"
                      data-testid="textarea-ai-prompt"
                    />
                  </div>

                  <div className="space-y-2">
                    <Label>Quick Prompts</Label>
                    <div className="grid grid-cols-1 gap-2">
                      {quickPrompts.map((prompt, index) => (
                        <Button
                          key={index}
                          variant="outline"
                          size="sm"
                          className="text-left h-auto p-3 justify-start whitespace-normal"
                          onClick={() => setAiPrompt(prompt)}
                          data-testid={`quick-prompt-${index}`}
                        >
                          {prompt}
                        </Button>
                      ))}
                    </div>
                  </div>

                  <div className="flex space-x-2">
                    <Button 
                      onClick={handleGenerateContent}
                      disabled={isGenerating || !aiPrompt.trim()}
                      className="flex-1"
                      data-testid="button-generate-content"
                    >
                      {isGenerating ? (
                        <>
                          <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                          Generating...
                        </>
                      ) : (
                        <>
                          <Wand2 className="w-4 h-4 mr-2" />
                          Generate Content
                        </>
                      )}
                    </Button>
                    {formData.content && (
                      <Button 
                        variant="outline" 
                        onClick={() => copyToClipboard(formData.content)}
                        data-testid="button-copy-content"
                      >
                        <Copy className="w-4 h-4" />
                      </Button>
                    )}
                  </div>
                </div>
              </TabsContent>

              {/* Preview Tab */}
              <TabsContent value="preview" className="space-y-4" data-testid="preview-content">
                <div className="prose prose-sm max-w-none">
                  <h1 className="text-3xl font-bold mb-4" data-testid="preview-title">
                    {formData.title || "Untitled Blog Post"}
                  </h1>
                  {formData.excerpt && (
                    <p className="text-lg text-muted-foreground mb-6 italic" data-testid="preview-excerpt">
                      {formData.excerpt}
                    </p>
                  )}
                  <div className="whitespace-pre-wrap" data-testid="preview-content-body">
                    {formData.content || "No content to preview. Start writing or use AI to generate content."}
                  </div>
                </div>
              </TabsContent>
            </div>
          </Tabs>
        </div>

        {/* Actions */}
        <div className="flex justify-between items-center pt-4 border-t">
          <div className="flex items-center space-x-2">
            {blog?.aiGenerated && (
              <Badge variant="secondary" className="flex items-center">
                <Sparkles className="w-3 h-3 mr-1" />
                AI Generated
              </Badge>
            )}
            {formData.published ? (
              <Badge variant="default">Published</Badge>
            ) : (
              <Badge variant="outline">Draft</Badge>
            )}
          </div>

          <div className="flex space-x-3">
            <Button 
              variant="outline" 
              onClick={() => onOpenChange(false)}
              data-testid="button-cancel-blog"
            >
              Cancel
            </Button>
            <Button 
              onClick={handleSave}
              disabled={saveBlogMutation.isPending || !formData.title.trim() || !formData.content.trim()}
              data-testid="button-save-blog"
            >
              {saveBlogMutation.isPending ? "Saving..." : (
                <>
                  <Save className="w-4 h-4 mr-2" />
                  {blog?.id ? "Update" : "Save"} Blog
                </>
              )}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/CreateGigModal.tsx.content:
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { DollarSign, Clock, Briefcase } from "lucide-react";

interface CreateGigModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onGigCreated: () => void;
}

export default function CreateGigModal({ open, onOpenChange, onGigCreated }: CreateGigModalProps) {
  const { toast } = useToast();
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    price: "",
    category: "",
    deliveryTime: "3",
    active: true,
  });

  const createGigMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/gigs", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Gig created successfully",
      });
      onGigCreated();
      resetForm();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create gig",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      title: "",
      description: "",
      price: "",
      category: "",
      deliveryTime: "3",
      active: true,
    });
  };

  const handleInputChange = (field: string, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleSubmit = () => {
    if (!formData.title.trim() || !formData.description.trim() || !formData.price) {
      toast({
        title: "Error",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    const price = parseFloat(formData.price);
    if (isNaN(price) || price <= 0) {
      toast({
        title: "Error",
        description: "Please enter a valid price",
        variant: "destructive",
      });
      return;
    }

    const deliveryTime = parseInt(formData.deliveryTime);
    if (isNaN(deliveryTime) || deliveryTime <= 0) {
      toast({
        title: "Error",
        description: "Please enter a valid delivery time",
        variant: "destructive",
      });
      return;
    }

    createGigMutation.mutate({
      title: formData.title,
      description: formData.description,
      price: formData.price,
      category: formData.category,
      deliveryTime: deliveryTime,
      active: formData.active,
    });
  };

  const categories = [
    { value: "design", label: "Design & Graphics" },
    { value: "development", label: "Programming & Development" },
    { value: "writing", label: "Writing & Translation" },
    { value: "marketing", label: "Digital Marketing" },
    { value: "video", label: "Video & Animation" },
    { value: "business", label: "Business Consulting" },
    { value: "lifestyle", label: "Lifestyle" },
    { value: "other", label: "Other" },
  ];

  const deliveryOptions = [
    { value: "1", label: "1 day" },
    { value: "2", label: "2 days" },
    { value: "3", label: "3 days" },
    { value: "5", label: "5 days" },
    { value: "7", label: "1 week" },
    { value: "14", label: "2 weeks" },
    { value: "21", label: "3 weeks" },
    { value: "30", label: "1 month" },
  ];

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto" data-testid="create-gig-modal">
        <DialogHeader>
          <DialogTitle>Create New Gig</DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Gig Title */}
          <div className="space-y-2">
            <Label htmlFor="gig-title">Gig Title *</Label>
            <Input
              id="gig-title"
              value={formData.title}
              onChange={(e) => handleInputChange("title", e.target.value)}
              placeholder="I will create a professional logo design for your business"
              data-testid="input-gig-title"
            />
            <p className="text-xs text-muted-foreground">
              Start with "I will..." and clearly describe what you offer
            </p>
          </div>

          {/* Description */}
          <div className="space-y-2">
            <Label htmlFor="gig-description">Description *</Label>
            <Textarea
              id="gig-description"
              value={formData.description}
              onChange={(e) => handleInputChange("description", e.target.value)}
              placeholder="Describe your service in detail. What will you deliver? What's included? What makes your service special?"
              className="min-h-[120px]"
              data-testid="textarea-gig-description"
            />
          </div>

          {/* Price and Delivery */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <Label htmlFor="gig-price">Starting Price (USD) *</Label>
              <div className="relative">
                <DollarSign className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                <Input
                  id="gig-price"
                  type="number"
                  min="5"
                  step="0.01"
                  value={formData.price}
                  onChange={(e) => handleInputChange("price", e.target.value)}
                  placeholder="25.00"
                  className="pl-10"
                  data-testid="input-gig-price"
                />
              </div>
              <p className="text-xs text-muted-foreground">Minimum $5</p>
            </div>

            <div className="space-y-2">
              <Label>Delivery Time *</Label>
              <Select value={formData.deliveryTime} onValueChange={(value) => handleInputChange("deliveryTime", value)}>
                <SelectTrigger data-testid="select-delivery-time">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {deliveryOptions.map((option) => (
                    <SelectItem key={option.value} value={option.value}>
                      <div className="flex items-center">
                        <Clock className="w-4 h-4 mr-2" />
                        {option.label}
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          {/* Category */}
          <div className="space-y-2">
            <Label>Category</Label>
            <Select value={formData.category} onValueChange={(value) => handleInputChange("category", value)}>
              <SelectTrigger data-testid="select-gig-category">
                <SelectValue placeholder="Choose a category" />
              </SelectTrigger>
              <SelectContent>
                {categories.map((category) => (
                  <SelectItem key={category.value} value={category.value}>
                    <div className="flex items-center">
                      <Briefcase className="w-4 h-4 mr-2" />
                      {category.label}
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Service Details */}
          <div className="p-4 bg-muted/50 rounded-lg space-y-4">
            <h4 className="font-medium">What's Included in Your Service</h4>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
              <div className="space-y-2">
                <p className="font-medium text-muted-foreground">Consider including:</p>
                <ul className="space-y-1 text-muted-foreground">
                  <li>â€¢ Number of revisions</li>
                  <li>â€¢ File formats provided</li>
                  <li>â€¢ Commercial use rights</li>
                  <li>â€¢ Source files inclusion</li>
                </ul>
              </div>
              <div className="space-y-2">
                <p className="font-medium text-muted-foreground">Professional tips:</p>
                <ul className="space-y-1 text-muted-foreground">
                  <li>â€¢ Be specific about deliverables</li>
                  <li>â€¢ Set clear expectations</li>
                  <li>â€¢ Mention your experience</li>
                  <li>â€¢ Add portfolio examples</li>
                </ul>
              </div>
            </div>
          </div>

          {/* Active Toggle */}
          <div className="flex items-center justify-between p-4 bg-muted/50 rounded-lg">
            <div>
              <Label htmlFor="gig-active">Active Gig</Label>
              <p className="text-sm text-muted-foreground">
                Make this gig available for orders immediately
              </p>
            </div>
            <Switch
              id="gig-active"
              checked={formData.active}
              onCheckedChange={(checked) => handleInputChange("active", checked)}
              data-testid="switch-gig-active"
            />
          </div>

          {/* Actions */}
          <div className="flex justify-end space-x-3 pt-4 border-t border-border">
            <Button 
              variant="outline" 
              onClick={() => onOpenChange(false)}
              data-testid="button-cancel-gig"
            >
              Cancel
            </Button>
            <Button 
              onClick={handleSubmit}
              disabled={createGigMutation.isPending}
              data-testid="button-create-gig"
            >
              {createGigMutation.isPending ? "Creating..." : "Create Gig"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/CreatePostModal.ts.content:
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { Image, Video, FileText, Upload, X } from "lucide-react";

interface CreatePostModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onPostCreated: () => void;
}

export default function CreatePostModal({ open, onOpenChange, onPostCreated }: CreatePostModalProps) {
  const { toast } = useToast();
  const { user } = useAuth();
  const [content, setContent] = useState("");
  const [postType, setPostType] = useState<"text" | "image" | "video">("text");
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [previewUrl, setPreviewUrl] = useState<string | null>(null);

  const createPostMutation = useMutation({
    mutationFn: async (formData: FormData) => {
      return apiRequest("POST", "/api/posts", formData);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Post created successfully",
      });
      onPostCreated();
      resetForm();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create post",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setContent("");
    setPostType("text");
    setSelectedFile(null);
    setPreviewUrl(null);
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    setSelectedFile(file);

    // Create preview URL
    const url = URL.createObjectURL(file);
    setPreviewUrl(url);

    // Set post type based on file type
    if (file.type.startsWith('image/')) {
      setPostType('image');
    } else if (file.type.startsWith('video/')) {
      setPostType('video');
    }
  };

  const removeFile = () => {
    setSelectedFile(null);
    setPreviewUrl(null);
    setPostType("text");
  };

  const handleSubmit = () => {
    if (!content.trim() && !selectedFile) {
      toast({
        title: "Error",
        description: "Please add content or attach a file",
        variant: "destructive",
      });
      return;
    }

    const formData = new FormData();
    formData.append("content", content);
    formData.append("postType", postType);

    if (selectedFile) {
      formData.append("media", selectedFile);
    }

    createPostMutation.mutate(formData);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl" data-testid="create-post-modal">
        <DialogHeader>
          <DialogTitle>Create New Post</DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Content */}
          <div className="space-y-2">
            <Label htmlFor="post-content">What's on your mind?</Label>
            <Textarea
              id="post-content"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              placeholder="Share your thoughts, ideas, or updates with the community..."
              className="min-h-[120px] resize-none"
              data-testid="textarea-post-content"
            />
            <div className="text-xs text-muted-foreground text-right">
              {content.length}/500
            </div>
          </div>

          {/* Post Type Selection */}
          <div className="space-y-2">
            <Label>Post Type</Label>
            <Select value={postType} onValueChange={(value: any) => setPostType(value)}>
              <SelectTrigger data-testid="select-post-type">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="text">
                  <div className="flex items-center">
                    <FileText className="w-4 h-4 mr-2" />
