import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import Navigation from "@/components/Navigation";
import Sidebar from "@/components/Sidebar";
import CreatePostModal from "@/components/CreatePostModal";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { 
  Heart,
  MessageCircle,
  Share2,
  Plus,
  MoreHorizontal
} from "lucide-react";

export default function Feed() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();
  const queryClient = useQueryClient();
  const [showCreatePost, setShowCreatePost] = useState(false);

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  // Fetch posts
  const { data: posts, isLoading: postsLoading } = useQuery({
    queryKey: ["/api/posts"],
    enabled: isAuthenticated,
  });

  // Like post mutation
  const likePostMutation = useMutation({
    mutationFn: async (postId: string) => {
      return apiRequest("POST", `/api/posts/${postId}/like`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to like post",
        variant: "destructive",
      });
    },
  });

  const handleLikePost = (postId: string) => {
    likePostMutation.mutate(postId);
  };

  const formatRelativeTime = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60));

    if (diffInHours < 1) return "Just now";
    if (diffInHours === 1) return "1h";
    if (diffInHours < 24) return `${diffInHours}h`;

    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays === 1) return "1d";
    if (diffInDays < 7) return `${diffInDays}d`;

    return date.toLocaleDateString();
  };

  if (isLoading || postsLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading feed...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return null;
  }

  return (
    <div className="min-h-screen bg-background">
      <Navigation />

      <div className="flex">
        <Sidebar />

        <main className="flex-1 min-h-screen bg-background">
          <div className="max-w-2xl mx-auto p-6">
            <div className="flex items-center justify-between mb-8">
              <h1 className="text-3xl font-bold text-foreground" data-testid="text-feed-title">Social Feed</h1>
              <Button onClick={() => setShowCreatePost(true)} data-testid="button-create-post">
                <Plus className="w-4 h-4 mr-2" />
                Create Post
              </Button>
            </div>

            {/* Posts */}
            <div className="space-y-6">
              {posts && posts.length > 0 ? (
                posts.map((post: any) => (
                  <Card key={post.id} data-testid={`post-${post.id}`}>
                    <CardContent className="p-6">
                      {/* Post Header */}
                      <div className="flex items-start justify-between mb-4">
                        <div className="flex items-center space-x-3">
                          <Avatar data-testid={`avatar-post-${post.id}`}>
                            <AvatarImage src={post.user?.profileImageUrl} />
                            <AvatarFallback>
                              {post.user?.firstName?.[0]}{post.user?.lastName?.[0]}
                            </AvatarFallback>
                          </Avatar>
                          <div>
                            <div className="flex items-center space-x-2">
                              <span className="font-semibold text-foreground" data-testid={`text-author-${post.id}`}>
                                {post.user?.firstName} {post.user?.lastName}
                              </span>
                              <span className="text-muted-foreground text-sm">
                                @{post.user?.email?.split('@')[0]}
                              </span>
                              <span className="text-muted-foreground text-sm">Â·</span>
                              <span className="text-muted-foreground text-sm" data-testid={`text-time-${post.id}`}>
                                {formatRelativeTime(post.createdAt)}
                              </span>
                            </div>
                          </div>
                        </div>
                        <Button variant="ghost" size="sm" data-testid={`button-more-${post.id}`}>
                          <MoreHorizontal className="w-4 h-4" />
                        </Button>
                      </div>

                      {/* Post Content */}
                      <div className="mb-4">
                        <p className="text-foreground whitespace-pre-wrap" data-testid={`text-content-${post.id}`}>
                          {post.content}
                        </p>
                        {post.mediaUrl && (
                          <img 
                            src={post.mediaUrl} 
                            alt="Post media" 
                            className="w-full mt-3 rounded-lg border border-border"
                            data-testid={`img-media-${post.id}`}
                          />
                        )}
                      </div>

                      {/* Post Actions */}
                      <div className="flex items-center space-x-6 pt-4 border-t border-border">
                        <Button 
                          variant="ghost" 
                          size="sm" 
                          onClick={() => handleLikePost(post.id)}
                          className="text-muted-foreground hover:text-red-500 transition-colors"
                          data-testid={`button-like-${post.id}`}
                        >
                          <Heart className="w-4 h-4 mr-1" />
                          <span>{post.likes || 0}</span>
                        </Button>
                        <Button 
                          variant="ghost" 
                          size="sm"
                          className="text-muted-foreground hover:text-blue-500 transition-colors"
                          data-testid={`button-comment-${post.id}`}
                        >
                          <MessageCircle className="w-4 h-4 mr-1" />
                          <span>{post.commentsCount || 0}</span>
                        </Button>
                        <Button 
                          variant="ghost" 
                          size="sm"
                          className="text-muted-foreground hover:text-green-500 transition-colors"
                          data-testid={`button-share-${post.id}`}
                        >
                          <Share2 className="w-4 h-4 mr-1" />
                          <span>Share</span>
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                ))
              ) : (
                <Card data-testid="empty-feed">
                  <CardContent className="p-12 text-center">
                    <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
                      <MessageCircle className="w-8 h-8 text-muted-foreground" />
                    </div>
                    <h3 className="text-lg font-semibold text-foreground mb-2">No posts yet</h3>
                    <p className="text-muted-foreground mb-4">Be the first to share something with the community!</p>
                    <Button onClick={() => setShowCreatePost(true)} data-testid="button-create-first-post">
                      <Plus className="w-4 h-4 mr-2" />
                      Create Your First Post
                    </Button>
                  </CardContent>
                </Card>
              )}
            </div>
          </div>
        </main>
      </div>

      {/* Create Post Modal */}
      <CreatePostModal 
        open={showCreatePost} 
        onOpenChange={setShowCreatePost}
        onPostCreated={() => {
          queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
          setShowCreatePost(false);
        }}
      />
    </div>
  );
}

client/src/pages/Gigs.tsx.content:
import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import Navigation from "@/components/Navigation";
import Sidebar from "@/components/Sidebar";
import CreateGigModal from "@/components/CreateGigModal";
import PayPalButton from "@/components/PayPalButton";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { 
  Plus,
  Search,
  Briefcase,
  DollarSign,
  Clock,
  Star,
  TrendingUp,
  Users,
  CheckCircle
} from "lucide-react";

export default function Gigs() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();
  const queryClient = useQueryClient();
  const [showCreateGig, setShowCreateGig] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  // Fetch gigs
  const { data: gigs, isLoading: gigsLoading } = useQuery({
    queryKey: ["/api/gigs"],
    enabled: isAuthenticated,
  });

  // Fetch user's gigs
  const { data: userGigs } = useQuery({
    queryKey: ["/api/users/gigs"],
    enabled: isAuthenticated,
  });

  // Order gig mutation
  const orderGigMutation = useMutation({
    mutationFn: async (gigId: string) => {
      return apiRequest("POST", `/api/gigs/${gigId}/order`);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Gig order created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/gigs"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create gig order",
        variant: "destructive",
      });
    },
  });

  const categories = [
    { value: "all", label: "All Categories" },
    { value: "design", label: "Design & Graphics" },
    { value: "development", label: "Programming" },
    { value: "writing", label: "Writing & Translation" },
    { value: "marketing", label: "Digital Marketing" },
    { value: "video", label: "Video & Animation" },
    { value: "business", label: "Business" },
  ];

  const filteredGigs = gigs?.filter((gig: any) => {
    const matchesSearch = gig.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         gig.description.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === "all" || gig.category === selectedCategory;
    return matchesSearch && matchesCategory && gig.active;
  }) || [];

  if (isLoading || gigsLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading gigs...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return null;
  }

  return (
    <div className="min-h-screen bg-background">
      <Navigation />

      <div className="flex">
        <Sidebar />

        <main className="flex-1 min-h-screen bg-background">
          <div className="p-6">
            <div className="max-w-7xl mx-auto">
              {/* Header */}
              <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-8">
                <div>
                  <h1 className="text-3xl font-bold text-foreground mb-2" data-testid="text-gigs-title">
                    Freelance Gigs
                  </h1>
                  <p className="text-muted-foreground">Offer your services and find great freelancers</p>
                </div>
                <div className="mt-4 md:mt-0 flex items-center space-x-4">
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                    <Input
                      placeholder="Search gigs..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      className="pl-10 w-64"
                      data-testid="input-search-gigs"
                    />
                  </div>
                  <Button 
                    onClick={() => setShowCreateGig(true)}
                    data-testid="button-create-gig"
                  >
                    <Plus className="w-4 h-4 mr-2" />
                    Create Gig
                  </Button>
                </div>
              </div>

              {/* Gig Stats */}
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
                <Card data-testid="card-stat-total-gigs">
                  <CardContent className="p-4 text-center">
                    <div className="flex items-center justify-center mb-2">
                      <Briefcase className="w-5 h-5 text-primary mr-2" />
                      <div className="text-2xl font-bold text-foreground">{gigs?.length || 0}</div>
                    </div>
                    <div className="text-sm text-muted-foreground">Available Gigs</div>
                  </CardContent>
                </Card>
                <Card data-testid="card-stat-my-gigs">
                  <CardContent className="p-4 text-center">
                    <div className="flex items-center justify-center mb-2">
                      <Users className="w-5 h-5 text-green-600 mr-2" />
                      <div className="text-2xl font-bold text-foreground">{userGigs?.length || 0}</div>
                    </div>
                    <div className="text-sm text-muted-foreground">My Gigs</div>
                  </CardContent>
                </Card>
                <Card data-testid="card-stat-total-orders">
                  <CardContent className="p-4 text-center">
                    <div className="flex items-center justify-center mb-2">
                      <CheckCircle className="w-5 h-5 text-blue-600 mr-2" />
                      <div className="text-2xl font-bold text-foreground">
                        {gigs?.reduce((total: number, gig: any) => total + (gig.orders || 0), 0) || 0}
                      </div>
                    </div>
                    <div className="text-sm text-muted-foreground">Total Orders</div>
                  </CardContent>
                </Card>
                <Card data-testid="card-stat-avg-price">
                  <CardContent className="p-4 text-center">
                    <div className="flex items-center justify-center mb-2">
                      <DollarSign className="w-5 h-5 text-yellow-600 mr-2" />
                      <div className="text-2xl font-bold text-foreground">
                        ${gigs?.length > 0 ? (gigs.reduce((total: number, gig: any) => total + parseFloat(gig.price || 0), 0) / gigs.length).toFixed(0) : 0}
                      </div>
                    </div>
                    <div className="text-sm text-muted-foreground">Avg Price</div>
                  </CardContent>
                </Card>
              </div>

              {/* Category Filter */}
              <div className="flex flex-wrap gap-2 mb-8">
                {categories.map((category) => (
                  <Button
                    key={category.value}
                    variant={selectedCategory === category.value ? "default" : "outline"}
                    size="sm"
                    onClick={() => setSelectedCategory(category.value)}
                    data-testid={`button-category-${category.value}`}
                  >
                    {category.label}
                  </Button>
                ))}
              </div>

              {/* Gigs Grid */}
              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredGigs.length > 0 ? (
                  filteredGigs.map((gig: any) => (
                    <Card key={gig.id} className="hover:shadow-lg transition-shadow" data-testid={`gig-${gig.id}`}>
                      <CardHeader>
                        <div className="flex items-start space-x-3">
                          <Avatar className="w-10 h-10" data-testid={`avatar-gig-${gig.id}`}>
                            <AvatarImage src={gig.user?.profileImageUrl} />
                            <AvatarFallback>
                              {gig.user?.firstName?.[0]}{gig.user?.lastName?.[0]}
                            </AvatarFallback>
                          </Avatar>
                          <div className="flex-1">
                            <div className="flex items-center space-x-2 mb-1">
                              <span className="font-semibold text-sm" data-testid={`text-seller-${gig.id}`}>
                                {gig.user?.firstName} {gig.user?.lastName}
                              </span>
                              <div className="flex items-center space-x-1">
                                <Star className="w-3 h-3 text-yellow-500 fill-current" />
                                <span className="text-xs text-muted-foreground" data-testid={`text-rating-${gig.id}`}>
                                  {parseFloat(gig.rating || 0).toFixed(1)}
                                </span>
                              </div>
                            </div>
                            <CardTitle className="text-lg line-clamp-2" data-testid={`text-gig-title-${gig.id}`}>
                              {gig.title}
                            </CardTitle>
                          </div>
                        </div>
                      </CardHeader>
                      <CardContent>
                        <p className="text-muted-foreground text-sm mb-4 line-clamp-3" data-testid={`text-description-${gig.id}`}>
                          {gig.description}
                        </p>

                        <div className="flex items-center justify-between mb-4">
                          {gig.category && (
                            <Badge variant="outline" className="text-xs" data-testid={`badge-category-${gig.id}`}>
                              {gig.category}
                            </Badge>
                          )}
                          <div className="flex items-center space-x-1 text-sm text-muted-foreground">
                            <Clock className="w-4 h-4" />
                            <span data-testid={`text-delivery-${gig.id}`}>
                              {gig.deliveryTime} day{gig.deliveryTime !== 1 ? 's' : ''}
                            </span>
                          </div>
                        </div>

                        <div className="flex items-center justify-between mb-4">
                          <div className="text-xl font-bold text-foreground" data-testid={`text-price-${gig.id}`}>
                            Starting at ${parseFloat(gig.price).toFixed(2)}
                          </div>
                          <div className="text-sm text-muted-foreground" data-testid={`text-orders-${gig.id}`}>
                            {gig.orders || 0} order{gig.orders !== 1 ? 's' : ''}
                          </div>
                        </div>

                        {/* Only show order button if not own gig */}
                        {gig.userId !== user?.id ? (
                          <PayPalButton
                            amount={gig.price}
                            currency="USD"
                            intent="CAPTURE"
                          />
                        ) : (
                          <Button 
                            variant="outline" 
                            className="w-full"
                            data-testid={`button-edit-gig-${gig.id}`}
                          >
                            Edit Gig
                          </Button>
                        )}
                      </CardContent>
                    </Card>
                  ))
                ) : (
                  <div className="col-span-full" data-testid="empty-gigs">
                    <Card>
                      <CardContent className="p-12 text-center">
                        <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
                          <Briefcase className="w-8 h-8 text-muted-foreground" />
                        </div>
                        <h3 className="text-lg font-semibold text-foreground mb-2">No gigs found</h3>
                        <p className="text-muted-foreground mb-4">
                          {searchQuery || selectedCategory !== "all" 
                            ? "Try adjusting your search or filter." 
                            : "Start offering your services to earn money!"}
                        </p>
                        <Button 
                          onClick={() => setShowCreateGig(true)}
                          data-testid="button-create-first-gig"
                        >
                          <Plus className="w-4 h-4 mr-2" />
                          Create Your First Gig
                        </Button>
                      </CardContent>
                    </Card>
                  </div>
                )}
              </div>
            </div>
          </div>
        </main>
      </div>

      {/* Create Gig Modal */}
      <CreateGigModal
        open={showCreateGig}
        onOpenChange={setShowCreateGig}
        onGigCreated={() => {
          queryClient.invalidateQueries({ queryKey: ["/api/gigs"] });
          queryClient.invalidateQueries({ queryKey: ["/api/users/gigs"] });
          setShowCreateGig(false);
        }}
      />
    </div>
  );
}

